import React, { Component } from 'react';
import Modal from 'react-modal';

import axios from 'axios';

import LunchroomModalComponent from './LunchroomModalComponent';

class LunchroomComponent extends Component {
  constructor(){
    super();
    this.state = {
      isActive:false
    }

    axios({
      url: 'http://35.229.97.157:5000/graphql/?',
      method: 'post',
      data: {
        query: `
          query{
            allStatistics{
              t{
                date
              }
            }
          }
          `
      }
    }).then((result) => {
      console.log(result.data)
    }).catch(error => {
      console.log(error)
    });

  }
  componentWillMount(){
    Modal.setAppElement('body');
  }

  toggleModal = () => {
    this.setState({
      isActive:!this.state.isActive
    })
  }
  render() {
    return (
      <div className="LunchroomComponent">
        <div className="col-md-3">
          <div className="thumbnail">
            <img src={ this.props.src }
            onClick={this.toggleModal}
            className="small"
            alt={ this.props.name }/>
            <div className="caption">
              <h4 style={{color:"#337ab7"}}>{ this.props.name }</h4>
              <p>{ this.props.ed }</p>
              <Modal isOpen={this.state.isActive}
              onRequestClose={this.toggleModal}>
                <LunchroomModalComponent
                name={this.props.name}
                src={this.props.src}/>
              </Modal>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default LunchroomComponent;
