import React, { Component } from 'react';
import Modal from 'react-modal';

import LunchroomModalComponent from './LunchroomModalComponent';

class LunchroomComponent extends Component {
  constructor(){
    super();
    this.state = {
      isActive:false
    }
  }
  componentWillMount(){
    Modal.setAppElement('body');
  }

  toggleModal = () => {
    this.setState({
      isActive:!this.state.isActive
    })
  }
  render() {
    return (
      <div className="LunchroomComponent">
        <div className="col-md-3">
          <div className="thumbnail">
            <img src={ this.props.src }
            className="small"
            alt={ this.props.name }/>
              <div className="caption">
                <h4>{ this.props.name }</h4>
                <p>{ this.props.ed }</p>
                <button onClick={this.toggleModal}>Clic</button>
                <Modal isOpen={this.state.isActive}>
                  <LunchroomModalComponent/>
                </Modal>
              </div>
          </div>
        </div>
      </div>
    );
  }
}

export default LunchroomComponent;
